import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

import { Nav } from "./data";

const Intro = () => {
    gsap.registerPlugin(ScrollTrigger);
    const introTextRef = useRef(null);
    const introNameRef = useRef(null);
    const [introNameVisible, setIntroNameVisible] = useState(false);

    useEffect(() => {
        const introbg = document.querySelector(".intro_bg");
        const introWrap = document.querySelector(".intro_wrap");

        const animateIntroBg = () => {
            gsap.to(introbg, {
                height: 0,
                delay: 2,
                duration: 1,
                ease: "expo.inOut",
                onComplete: () => {
                    introTextRef.current.classList.add("active");
                    setTimeout(() => {
                        introTextRef.current.classList.remove("active");
                        setIntroNameVisible(true); // intro_name을 보이도록 상태 업데이트
                    }, 3000);
                },
            });
        };

        ScrollTrigger.create({
            trigger: introbg,
            start: "top 80%",
            end: "top 20%",
            onEnter: () => animateIntroBg(),
        });

        // ScrollTrigger를 사용하여 intro_name 애니메이션 적용
        ScrollTrigger.create({
            trigger: introWrap,
            start: "top 50%", // 적절한 시작 지점으로 조정
            onEnter: () => {
                gsap.to(introNameRef.current, {
                    top: "20px",
                    left: "20px",
                    fontSize: "3rem",
                    position: "fixed",
                    duration: 1,
                });

                // nav 애니메이션
                gsap.to(".nav", {
                    paddingLeft: "10px",
                    fontSize: "0.95rem",
                    duration: 1,
                });
            },
        });
    }, []);

    return (
        <>
            <div className="intro_bg"></div>
            <div id="intro" className="item">
                <nav className="nav">
                    {/* ... */}
                </nav>
                <div className="intro_wrap" ref={introTextRef}>
                    {/* ... */}
                </div>
                <div className="intro_bottom">
                    <span
                        className={`intro_name${introNameVisible ? " scrolled" : ""}`}
                        ref={introNameRef}
                    >
                        Heeyon Kim
                    </span>
                </div>
            </div>
        </>
    );
};

export default Intro;
