import React, { useEffect, useRef } from "react";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import { headerNav } from "../constants";

const Header = () => {
    const navmoveRef = useRef(null);
    const navmoveitemRef = useRef([]);

    useEffect(() => {
        gsap.registerPlugin(ScrollTrigger);

        const navmove = navmoveRef.current;
        const navmoveitem = navmoveitemRef.current;

        let links = gsap.utils.toArray(".header_nav");

        links.forEach((link) => {
            let element = document.querySelector(link.getAttribute("href"));

            // 스크롤 트리거 생성
            ScrollTrigger.create({
                trigger: element,
                start: "top top",
                onToggle: (self) => setActive(link, links),
            });

            // 클릭 시 스크롤 이동
            link.addEventListener("click", (e) => {
                e.preventDefault();
                gsap.to(window, {
                    duration: 1,
                    scrollTo: { y: linkST.start, offsetY: 80 },
                    overwrite: "auto",
                });
            });
        });

        // 메뉴 액티브 설정
        function setActive(activeLink, allLinks) {
            allLinks.forEach((el) => el.classList.remove("active"));
            activeLink.classList.add("active");
        }

        // 메뉴 축소 효과
        ScrollTrigger.create({
            start: "top -80",
            end: 99999,
            toggleClass: {
                className: "is-active",
                targets: ".header_nav",
            },
        });
    }, []); // useEffect는 마운트될 때 한 번만 실행되도록 빈 배열을 전달합니다.

    return (
        <header id="header" ref={navmoveRef}>
            <div className="header_wrap">
                <div className="header_logo">
                    <a href="/">Constantly evolving, <br /> New front-end developers.</a>
                </div>
                <nav className="header_nav" ref={navmoveitemRef}>
                    <ul>
                        {headerNav.map((nav, i) => (
                            <li key={i}>
                                <a className="header_nav" href={nav.id}>
                                    {nav.title}
                                </a>
                            </li>
                        ))}
                        <li>( Menu )</li>
                    </ul>
                </nav>
            </div>
        </header>
    );
};

export default Header;
