import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

import { Nav } from "./data";

const Intro = () => {

    useEffect(() => {
        gsap.registerPlugin(ScrollTrigger);

        gsap.to(".intro_bg", {
            duration: 2,
            height: 0,
            delay: 2, // 2초 후에 시작
            ease: "power2.inOut", // 원하는 이징 함수 선택
        });

        // .nav, .intro_text_item, .intro_name 초기 설정
        gsap.set([".nav", ".intro_text_item", ".intro_name"], {
            opacity: 0,
            y: 50, // 처음에 아래로 숨김
        });

        gsap.to([".nav", ".intro_text_item", ".intro_name"], {
            duration: 1,
            opacity: 1,
            y: 0,
            stagger: 0.2, // 각 요소가 순차적으로 애니메이션 시작
            ease: "power2.out", // 원하는 이징 함수 선택
            scrollTrigger: {
                trigger: ".intro_name", // 시작할 요소
                start: "top center", // 스크롤 시작 위치
            },
        });

        gsap.to(".nav", {
            duration: 1,
            position: "fixed",
            top: 20,
            right: 20,
            paddingLeft: 10,
            fontSize: "1rem",
            scrollTrigger: {
                trigger: "#intro", // 시작할 요소
                start: "top center", // 스크롤 시작 위치
            },
        });

        // .intro_name 애니메이션
        gsap.to(".intro_name", {
            duration: 1,
            position: "fixed",
            top: 20,
            left: 20,
            fontSize: "1rem",
            scrollTrigger: {
                trigger: "#intro", // 시작할 요소
                start: "top center", // 스크롤 시작 위치
            },
        });
    }, []);

    return (
        <>
            <div className="intro_bg"></div>
            <div id="intro" className="item">
                <div className="intro_top">
                    <span className='intro_name'>
                        Heeyon Kim
                    </span>
                </div>
                <div className="intro_wrap">
                    <p className="intro_text_item">
                        Constantly<br />evolving,<br />
                        New<br />front-end<br />developers.
                    </p>
                </div>
                <nav className="nav">
                    <ul>
                        {Nav.map((nav, i) => (
                            <li key={i}>
                                <Link to={nav.id}>
                                    {nav.title}
                                </Link>
                            </li>
                        ))}
                    </ul>
                </nav>
            </div>
        </>
    );
};

export default Intro;
