import { useEffect, useState } from "react";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

import about_img from "../assets/img/aboutimg.jpg";
import aboutData from "data"

const About = () => {

  const aboutData = useState(aboutData);

  useEffect(() => {
    gsap.set('.aniitem', { opacity: 0, x: 100, y: 0 }); // 초기 상태 설정

    const animate = (item) => {
      gsap.to(item, {
        autoAlpha: 1,
        x: 0,
        y: 0,
        duration: 1,
        ease: 'expo',
      });
    };

    const items = gsap.utils.toArray('.aniitem');

    items.forEach((item, index) => {
      ScrollTrigger.create({
        trigger: item,
        start: 'top 80%',
        end: 'bottom 20%',
        onEnter: () => animate(item), // 요소가 보이면 애니메이션 실행
        delay: index * 0.1, // 순차적인 딜레이 적용
      });
    });
  }, []);

  return (
    <section id="about" className="item" data-bgcolor="#f6f5f2">
      <div className="about_wrap">
        <div className="about_left">
          <img src={about_img} alt="about_img" className="aniitem" />
        </div>
        <div className="about_right">
          {aboutData.map((about, i) => (
            <div className="about aniitem" key={i}>
              {about.title}
            </div>
          ))}
          <div className="about_text">
            {aboutData.map((about, i) => (
              <div className="aniitem" key={i}>
                {about.content.map((item, i) => (
                  <p key={i}>{item}</p>
                ))}
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default About;
