import React, { useEffect, useRef } from "react";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

const Intro = () => {

    // 인트로 효과
    gsap.registerPlugin(ScrollTrigger);

    useEffect(() => {
        gsap.to(".intro_bg")((introbg) => {
            ScrollTrigger.create({
                trigger: "intro_bg",
                start: "top 80%",
                end: "bottom 20%",
                markers: true,
                onEnter: () => {animate(introbg)}, 
            });

            introbg.style.opacity = "0";

        const animate = (introbg) => {
            gsap.fromTo(introbg, 
                {autoAlpha: 0, x: 100, y: 0}, 
                {autoAlpha: 1, x: 0, y: 0, duration: 1.25, overwrite: "auto", ease: "expo"}
            );
        }
        });
    });

    return (
        <>
            <div className="intro_bg"></div>
            <div id="intro" className="item">
                <div className="intro_text">
                    <p>Constantly</p>
                    <p>evolving,</p>
                    <p>New</p>
                    <p>front-end</p>
                    <p>developers.</p>
                </div>
            </div>

        </>
    );
};

export default Intro;
