import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

import { projectData } from "./data";
const projectbg = ["#212125", "#A88462", "#212125"];

const Project = () => {
  //프로젝트 배경 전환
  useEffect(() => {
    gsap.registerPlugin(ScrollTrigger);

    projectData.forEach((project, i) => {
      let color = projectbg[i];

      ScrollTrigger.create({
        trigger: `.project:nth-child(${i + 1})`,
        start: "top 50%",
        end: "bottom 50%",

        onEnter: () =>
          gsap.to("body", {
            backgroundColor: color,
            duration: 1.4,
          }),
        onEnterBack: () =>
          gsap.to("body", {
            backgroundColor: color,
            duration: 1.4,
          }),
      });
    });
  }, []);

  return (
    <section id="project" className="item" data-bgcolor="#212125">
      <div className="project_wrap">
        {projectData.map((project, i) => (
          <div key={i} className="project" data-bgcolor={projectbg[i]}>
            <div className="project_url_wrap">
              <Link to={project.url} className="project_url">
                {project.img}
                <p className="project_logo">{project.logo}</p>
              </Link>
            </div>
            <div className="project_info">
              <span>{project.title}</span>
              <span>{project.type}</span>
              <span>{project.info}</span>
            </div>
          </div>
        ))}

      </div>
    </section>
  );
};

export default Project;
