import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

import { Nav } from "./data";

const Intro = () => {
    const [elementsOpacity, setElementsOpacity] = useState(0);

    useEffect(() => {
        gsap.registerPlugin(ScrollTrigger);

        gsap.to(".intro_bg", {
            duration: 2,
            height: 0,
            delay: 2, //2초후 시작
            ease: "power2.inOut",
            onComplete: () => {
                setElementsOpacity(1); // 텍스트들 보이게

                // 2초 뒤에 animateText() 함수 실행
                setTimeout(() => {
                    animateText();
                }, 2000); // 2초 대기 후 실행
            },
        });

        const animateText = () => {
            gsap.fromTo(
                ".intro_name",
                {
                    fontSize: "5em",
                },
                {
                    fontSize: "1rem",
                    position: "fixed",
                    top: "3%",
                    left: "3%",
                }
            );

            gsap.fromTo(
                ".nav",
                {
                    height: "100%",
                },
                {
                    height: "80px",
                    position: "fixed",
                    top: "3%",
                    right: "3%",
                }
            );

            gsap.To(".nav ul li", { display: "inline-block" });

            gsap.fromTo(
                ".nav ul li a",
                {
                    fontSize: "4em",
                },
                {
                    fontSize: "0.9rem",
                    paddingLeft: "10px",
                }
            );

            gsap.fromTo(
                ".intro_text_item",
                {
                    opacity: 1,
                },
                {
                    opacity: 0,
                }
            );
        };


    }, []);

    return (
        <>
            <div className="intro_bg"></div>
            <div id="intro" className="item">
                <div className="intro_top">
                    <span className='intro_name' style={{ opacity: elementsOpacity }}>
                        Heeyon Kim
                    </span>
                </div>
                <div className="intro_wrap">
                    <p className="intro_text_item" style={{ opacity: elementsOpacity }}>
                        Constantly<br />evolving,<br />
                        New<br />front-end<br />developers.
                    </p>
                </div>
                <nav className="nav" style={{ opacity: elementsOpacity }}>
                    <ul>
                        {Nav.map((nav, i) => (
                            <li key={i}>
                                <Link to={nav.id}>
                                    {nav.title}
                                </Link>
                            </li>
                        ))}
                    </ul>
                </nav>
            </div>
        </>
    );
};

export default Intro;
