import React, { useEffect, useRef } from "react";
import { headerNav } from "../constants";

import gsap from 'gsap';
import ScrollTrigger from 'gsap/ScrollTrigger';

const Header = () => {
    const navmov = useRef(null);

    gsap.registerPlugin(ScrollTrigger);

    useEffect(() => {
        // gsap의 스크롤 트리거 애니메이션 생성
        const tl = gsap.timeline();

        // 헤더의 초기 스타일 설정
        gsap.set(navmov.current, {
            fontSize: '1rem', // 초기값 설정
            padding: '0px', // 초기값 설정
            y: 0, // 초기값 설정
        });

        // 스크롤 위치가 800px에 도달하면 스타일 변경 애니메이션 추가
        tl.to(navmov.current, {
            fontSize: '0.8rem',
            padding: '10px',
            y: "50%", // 세로 중간 좌표로 이동
        });

        ScrollTrigger.create({
            trigger: navmov.current,
            start: 'top 90vh',
            toggleActions: "play none none reverse",
            scrub: true,
            animation: tl, // 애니메이션을 ScrollTrigger에 연결
        });
    }, []);

    return (
        <header id="header">
            <div className="header_wrap">
                <div className="header_logo">
                    <a href="/">Constantly evolving, <br /> New front-end developers.</a>
                </div>
                {/* ref를 navmov로 설정하여 헤더 nav 엘리먼트에 접근 가능하도록 함 */}
                <nav className="header_nav" ref={navmov}>
                    <ul>
                        {headerNav.map((nav, i) => (
                            <li key={i}>
                                <a className="header_nav">
                                    {nav.title}
                                </a>
                            </li>
                        ))}
                        <li>( Menu )</li>
                    </ul>
                </nav>
            </div>
        </header>
    );
};

export default Header;
