import React, { useEffect } from "react";

import footerimg from "../img/ef_img1.jpg"
import backtop from "../img/back-top.png"

const Contact = () => {

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  }

  return (
    <footer id="contact" className="item" data-bgcolor="#e5e3db">
      <div className="contact_left">
        <img src={footerimg} alt="footerimg" />
      </div>
      <div className="contact_center">
        <p>` HEEPOLIO `</p>
        <p>Heeyon Kim</p>
        <p className="email">lightram23@gmail.com</p>
      </div>
      <div className="contact_right">
        <div className="backtop_box" onClick={scrollToTop}>
          <img src={backtop} alt="backtop" />
        </div>
      </div>
    </footer>
  );
};

export default Contact;
