import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

import { Nav } from "./data";

const Intro = () => {
    const [elementsOpacity, setElementsOpacity] = useState(0);

    useEffect(() => {
        gsap.registerPlugin(ScrollTrigger);

        const introTimeline = gsap.timeline({
            onComplete: () => {
                setElementsOpacity(1); // Text elements become visible
                animateText(); // Trigger text animation
            },
        });

        introTimeline.to(".intro_bg", {
            duration: 2,
            height: 0,
            delay: 2,
            ease: "power2.inOut",
        });

        // Text animation function
        const animateText = () => {
            gsap.fromTo(
                ".intro_name",
                { x: 0, y: 0, fontSize: "2rem", opacity: 1 },
                { x: 20, y: 20, fontSize: "1rem", opacity: 1, position: "fixed", duration: 1 }
            );

            gsap.fromTo(
                ".nav",
                { x: 0, y: 0, opacity: 1 },
                { x: -20, y: 20, opacity: 1, position: "fixed", duration: 1 }
            );

            gsap.fromTo(
                ".nav ul li",
                { display: "inline-block" },
                { display: "inline-block", duration: 1 }
            );

            gsap.fromTo(
                ".nav ul li a",
                { fontSize: "1rem", paddingLeft: 0 },
                { fontSize: "0.9rem", paddingLeft: "10px", duration: 1 }
            );

            gsap.fromTo(
                ".intro_text_item",
                { display: "block",  opacity: 1 },
                { display: "none",  opacity: 0, duration: 1 }
            );
        };
    }, []);

    return (
        <>
            <div className="intro_bg"></div>
            <div id="intro" className="item">
                <div className="intro_top">
                    <span className='intro_name' style={{ opacity: elementsOpacity }}>
                        Heeyon Kim
                    </span>
                </div>
                <div className="intro_wrap">
                    <p className="intro_text_item" style={{ opacity: elementsOpacity }}>
                        Constantly<br />evolving,<br />
                        New<br />front-end<br />developers.
                    </p>
                </div>
                <nav className="nav" style={{ opacity: elementsOpacity }}>
                    <ul>
                        {Nav.map((nav, i) => (
                            <li key={i}>
                                <Link to={nav.id}>
                                    {nav.title}
                                </Link>
                            </li>
                        ))}
                    </ul>
                </nav>
            </div>
        </>
    );
};

export default Intro;
