import React, { useEffect } from "react";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

const Intro = () => {
    gsap.registerPlugin(ScrollTrigger);

    useEffect(() => {
        const introbg = document.querySelector(".intro_bg"); // introbg 선택자를 수정

        const animate = () => {
            gsap.fromTo(
                introbg,
                { autoAlpha: 1, y: 0 },
                { autoAlpha: 0, y: -100, duration: 3, overwrite: "auto", ease: "expo" }
            );
        };

        ScrollTrigger.create({
            trigger: introbg,
            start: "top 80%",
            end: "bottom 20%",
            markers: true,
            onEnter: () => animate(),
        });
    }, []); // 두 번째 인자로 빈 배열을 전달하여 마운트 시 한 번만 실행되도록 설정

    return (
        <>
            <div className="intro_bg"></div>
            <div id="intro" className="item">
                <div className="intro_text">
                    <p>Constantly</p>
                    <p>evolving,</p>
                    <p>New</p>
                    <p>front-end</p>
                    <p>developers.</p>
                </div>
            </div>

        </>
    );
};

export default Intro;
