import React, { useEffect, useRef } from "react";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import { headerNav } from "../constants";

const Header = () => {
    const navmoveRef = useRef(null);
    const navmoveitemRef = useRef([]);

    useEffect(() => {
        gsap.registerPlugin(ScrollTrigger);

        const navmove = navmoveRef.current;
        const navmoveitem = navmoveitemRef.current;

        // 스크롤 트리거 설정 및 클릭 이벤트 설정
        const links = gsap.utils.toArray(navmoveitem);

        links.forEach((link) => {
            const target = document.querySelector(link.getAttribute("href"));
            const linkST = ScrollTrigger.create({
                trigger: target,
                start: "top top",
                onToggle: (self) => {
                    setActive(link, links);
                },
            });

            link.addEventListener("click", (e) => {
                e.preventDefault();
                gsap.to(window, {
                    duration: 1,
                    scrollTo: { y: linkST.start, offsetY: 80 },
                    overwrite: "auto",
                });
            });
        });

        return () => {
            links.forEach((link) => {
                link.removeEventListener("click", handleLinkClick);
            });
        };
    }, []);

    const setActive = (activeLink, allLinks) => {
        allLinks.forEach((link) => {
            link.classList.toggle("active", link === activeLink);
        });
    };

    const handleLinkClick = (e, linkST) => {
        e.preventDefault();
        gsap.to(window, {
            duration: 1,
            scrollTo: { y: linkST.start, offsetY: 80 },
            overwrite: "auto",
        });
    };

    return (
        <header id="header" ref={navmoveRef}>
            <div className="header_wrap">
                <div className="header_logo">
                    <a href="/">Constantly evolving, <br /> New front-end developers.</a>
                </div>
                <nav className="header_nav" ref={navmoveitemRef}>
                    <ul>
                        {headerNav.map((nav, i) => (
                            <li key={i}>
                                <a className="header_nav" href={nav.id}>
                                    {nav.title}
                                </a>
                            </li>
                        ))}
                        <li>( Menu )</li>
                    </ul>
                </nav>
            </div>
        </header>
    );
};

export default Header;
