import React, { useEffect } from "react";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

const Intro = () => {
    // 인트로 bg 검정 로딩 화면
    gsap.registerPlugin(ScrollTrigger);

    useEffect(() => {
        const introbg = document.querySelector(".intro_bg");
        const introText = document.querySelector(".intro_text");
        const paragraphs = introText.querySelectorAll("p");

        // 아래에서 위로 사라짐
        const animateIntroBg = () => {
            gsap.to(introbg, {
                height: 0,
                delay: 2,
                duration: 1,
                ease: "expo.inOut",
                onComplete: animateIntroText,
            });
        };

        const animateIntroText = () => {
            gsap.fromTo(paragraphs, 
                {autoAlpha: 0, x: 100, y: 0}, 
                {autoAlpha: 1, x: 0, y: 0, duration: 0.5, overwrite: "auto", ease: "expo"}
            );
        };

        ScrollTrigger.create({
            trigger: introbg,
            start: "top 80%",
            end: "top 20%",
            onEnter: () => animateIntroBg(),
        });
    }, []);

    return (
        <>
            <div className="intro_bg"></div>
            <div id="intro" className="item">
                <div className="intro_text">
                    <p className="intro_text_item">Constantly</p>
                    <p className="intro_text_item">evolving,</p>
                    <p className="intro_text_item">New</p>
                    <p className="intro_text_item">front-end</p>
                    <p className="intro_text_item">developers.</p>
                </div>
            </div>

        </>
    );
};

export default Intro;
