import React, { useEffect } from "react";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

import footerimg from "../img/ef_img1.jpg"
import backtop from "../img/back-top.png"

const Contact = () => {
  useEffect(() => {
    gsap.registerPlugin(ScrollTrigger);

    // 이미지 크기 및 투명도 애니메이션
    const imgAnimation = gsap.timeline({
      scrollTrigger: {
        trigger: "#contact",
        start: "top center",
        end: "bottom center",
        scrub: true,
      },
    });

    imgAnimation
      .to(".backtop_box img", { scale: 2, autoAlpha: 0 })
      .to(".backtop_box img", { scale: 1, autoAlpha: 1 });

  }, []);

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  }

  return (
    <footer id="contact" className="item" data-bgcolor="#e5e3db">
      <div className="contact_left">
        <img src={footerimg} alt="footerimg" />
      </div>
      <div className="contact_center">
        <p>` HEEPOLIO `</p>
        <p>Heeyon Kim</p>
        <p className="email">lightram23@gmail.com</p>
      </div>
      <div className="contact_right">
        <div className="backtop_box" onClick={scrollToTop}>
          <img src={backtop} alt="backtop" />
        </div>
      </div>
    </footer>
  );
};

export default Contact;
