import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

import { Nav } from "./data";

const Intro = () => {

    useEffect(() => {
        gsap.registerPlugin(ScrollTrigger);

        gsap.to(".intro_bg", {
            duration: 2,
            height: 0,
            delay: 2, // 2초 후에 시작
            ease: "power2.inOut", // 원하는 이징 함수 선택
            onComplete: () => {
                // 이 애니메이션 끝난 다음 시작
                animateElements();
            },
        });

        const animateElements = () => {
            gsap.from(".intro_name, .intro_text_item, .nav", {
                opacity: 0,
                y: 50,
                duration: 1,
                ease: "power2.out",
                stagger: 0.4,
                onComplete: () => {
                    gsap.set(".intro_name, .intro_text_item, .nav", {
                        opacity: 1,
                    });
                },
            });
        };

    }, []);

    return (
        <>
            <div className="intro_bg"></div>
            <div id="intro" className="item">
                <div className="intro_top">
                    <span className='intro_name'>
                        Heeyon Kim
                    </span>
                </div>
                <div className="intro_wrap">
                    <p className="intro_text_item">
                        Constantly<br />evolving,<br />
                        New<br />front-end<br />developers.
                    </p>
                </div>
                <nav className="nav">
                    <ul>
                        {Nav.map((nav, i) => (
                            <li key={i}>
                                <Link to={nav.id}>
                                    {nav.title}
                                </Link>
                            </li>
                        ))}
                    </ul>
                </nav>
            </div>
        </>
    );
};

export default Intro;
