import React, { useEffect, useRef } from "react";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

import Intro from "./components/Intro";
import About from "./components/About";
import Project from "./components/Project";
import Contact from "./components/Contact";

const View = () => {

  // 배경화면 효과
  gsap.registerPlugin(ScrollTrigger);
  const itembgcRef = useRef(null);

  useEffect(()=> {
    gsap.registerPlugin(ScrollTrigger);

    // 인트로 배경 애니메이션
    gsap.to(".intro_bg", {
        duration: 1,
        height: 0,
        delay: 2, //2초 뒤에 시작
        ease: "power2.inOut",
        onComplete: () => {
            //인트로 배경 애니메이션 끝난 후 텍스트 Opacity
            setElementsOpacity(1);
        },
    });

    gsap.utils.toArray(".item").forEach((item) => {
      let color = item.getAttribute("data-bgcolor");

      ScrollTrigger.create({
          trigger: item,
          start: "top 50%",
          end: "bottom 50%",
          // markers: true,

          onEnter: () => gsap.to("body", {
              backgroundColor: color,
              duration: 1.4,
          }),
          onEnterBack: () => gsap.to("body", {
              backgroundColor: color,
              duration: 1.4,
          }),
      });
    });
  });

  return (
    <>
      <div className="intro_bg"></div>
      <Intro itembgcRef={itembgcRef}/>
      <About itembgcRef={itembgcRef}/>
      <Project itembgcRef={itembgcRef}/>
      <Contact itembgcRef={itembgcRef}/>
    </>
  );
};

export default View;
