import React, { useEffect, useRef } from "react";
import { headerNav } from "../constants";

import gsap from 'gsap';
import ScrollTrigger from 'gsap/ScrollTrigger';

const Header = () => {
    const navmov = useRef(null);

    gsap.registerPlugin(ScrollTrigger);

    useEffect(() => {
        gsap.utils.toArray('.header_nav').forEach(navmov => {
            gsap.to(navmov, {
                opacity: 1,
                y: 100,
                scrollTrigger: {
                    trigger: navmov,
                    start: 'top center',
                    end: 'bottom center',
                    scrub: true,
                    markers: true,
                },
            });
        });

        // gsap를 사용하여 .header_nav의 너비를 30px로 줄이는 애니메이션을 추가합니다.
        gsap.to(navmov.current, {
            width: 30,
            duration: 1, // 애니메이션 지속 시간
        });

    }, []);

    return (
        <header id="header">
            <div className="header_wrap">
                <div className="header_logo">
                    <a href="/">Constantly evolving, <br /> New front-end developers.</a>
                </div>
                <nav className="header_nav" ref={navmov}>
                    <ul>
                        {headerNav.map((nav, i) => (
                            <li key={i}>
                                <a className="header_nav">
                                    {nav.title}
                                </a>
                            </li>
                        ))}
                        <li>( Menu )</li>
                    </ul>
                </nav>
            </div>
        </header>
    );
};

export default Header;
