import React, { useEffect } from "react";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

const Intro = () => {
    gsap.registerPlugin(ScrollTrigger);

    useEffect(() => {
        const introbg = document.querySelector(".intro_bg");

        const animate = () => {
            gsap.to(introbg, {
                height: 0,
                duration: 1, // 사라지는 시간
                ease: "expo.inOut", // 이징 함수 설정
            });
        };

        ScrollTrigger.create({
            trigger: introbg,
            start: "top 80%",
            end: "top 20%",
            markers: true,
            onEnter: () => animate(),
        });
    }, []);

    return (
        <>
            <div className="intro_bg"></div>
            <div id="intro" className="item">
                <div className="intro_text">
                    <p>Constantly</p>
                    <p>evolving,</p>
                    <p>New</p>
                    <p>front-end</p>
                    <p>developers.</p>
                </div>
            </div>

        </>
    );
};

export default Intro;
