import React from "react";
import { Link } from "react-router-dom";
import { headerNav } from "../constants";

import main_img from "../assets/img/main_img.jpg"

const Intro = ({ navActive }) => {
    return (
        <header id="intro">
            <div className="header_wrap">
                <div className="header_logo">
                    <a href="/">Constantly evolving, <br /> New front-end developers.</a>
                </div>
                <nav className={`header_nav ${navActive ? "active" : ""}`}>
                    <ul>
                        {headerNav.map((nav, i) => (
                            <li key={i}>
                                <Link to={nav.id} className={navActive ? "active" : ""}>
                                    {nav.title}
                                </Link>
                            </li>
                        ))}
                    </ul>
                </nav>
            </div>
            <div className="intro_wrap">
                <span className="intro_text">Heeyon Kim</span>
                <div className="intro_img">
                    <img src={main_img} alt="main_img" />
                </div>
            </div>
        </header>
    );
};

export default Intro;
