import React, { useEffect, useRef } from "react";
import { headerNav } from "../constants";

import gsap from 'gsap';
import ScrollTrigger from 'gsap/ScrollTrigger';
import { ScrollToPlugin } from 'gsap/ScrollToPlugin';

// ScrollTrigger, ScrollToPlugin 플러그인을 GSAP에 등록
gsap.registerPlugin(ScrollTrigger);
gsap.registerPlugin(ScrollToPlugin);

const Header = () => {
    const navmov = useRef(null);

    useEffect(() => {
    gsap.utils.toArray('.header_nav').forEach(navmov => {
      gsap.to(navmov, {
        opacity: 1,
        y: 100,
        scrollTrigger: {
          trigger: navmov,
          start: 'top center', // 애니메이션 시작
          end: 'bottom center', // 끝나는 시점
          scrub: true, // 스크롤에 따라 부드럽게 애니메이션 조정
          markers: true,
        },
      });
    });

    }, []);

    return (
        <header id="header">
            <div className="header_wrap">
                <div className="header_logo">
                    <a href="/">Constantly evolving, <br /> New front-end developers.</a>
                </div>
                <nav className="header_nav" ref={navmov}>
                    <ul>
                        {headerNav.map((nav, i) => (
                            <li key={i}>
                                <a className="header_nav">
                                    {nav.title}
                                </a>
                            </li>
                        ))}
                        <li>( Menu )</li>
                    </ul>
                </nav>
            </div>
        </header>
    );
};

export default Header;
