import React, { useEffect, useRef } from "react";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

const Intro = () => {
    gsap.registerPlugin(ScrollTrigger);
    const introTextRef = useRef(null);

    useEffect(() => {
        const introbg = document.querySelector(".intro_bg");
        const introWrap = document.querySelector(".intro_wrap");

        const animateIntroBg = () => {
            gsap.to(introbg, {
                height: 0,
                delay: 2,
                duration: 1,
                ease: "expo.inOut",
                onComplete: () => {
                    introTextRef.current.classList.add("active");
                    setTimeout(() => {
                        introTextRef.current.classList.remove("active");
                    }, 3000); // 2초 후에 active 클래스 제거
                },
            });
        };

        ScrollTrigger.create({
            trigger: introbg,
            start: "top 80%",
            end: "top 20%",
            onEnter: () => animateIntroBg(),
        });
    }, []);

    return (
        <>
            <div className="intro_bg"></div>
            <div id="intro" className="item">
                <div className="intro_wrap" ref={introTextRef}>
                    <p className="intro_text_item">
                        Constantly<br />evolving,<br />
                        New<br />front-end<br />developers.
                    </p>
                </div>
            </div>

        </>
    );
};

export default Intro;
